import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { speak } from '../utils/voiceUtils';

const Login = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [step, setStep] = useState(0); // 0: username, 1: password, 2: submit
  const stepRef = useRef(step);

  useEffect(() => {
    stepRef.current = step;
  }, [step]);

  useEffect(() => {
    speak('Welcome to login. Say your username.');

    const handleCommand = (e) => {
      const command = e.detail;
      if (command.includes('reset')) {
        setUsername('');
        setPassword('');
        setStep(0);
        speak('Resetting. Please say your username.');
        return;
      }

      if (stepRef.current === 0) {
        setUsername(command);
        setStep(1);
        speak(`Username set to ${command}. Now say your password.`);
      } else if (stepRef.current === 1) {
        setPassword(command);
        setStep(2);
        speak('Password set. Say login to submit.');
      } else if (stepRef.current === 2 && command.includes('login')) {
        handleLogin();
      } else {
        speak('Command not recognized. Please try again or say reset to start over.');
      }
    };

    window.addEventListener('voiceCommand', handleCommand);
    return () => window.removeEventListener('voiceCommand', handleCommand);
    // eslint-disable-next-line
  }, []);

  const handleLogin = async () => {
    try {
      const res = await axios.post('/auth/login', { username, password });
      localStorage.setItem('token', res.data.token);
      setIsLoggedIn(true);
      speak('Login successful. Welcome!');
    } catch (err) {
      speak('Login failed. Try again.');
      setStep(0);
    }
  };

  return <div aria-live="polite">Login Page (Voice Controlled)</div>;
};

export default Login;
