import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';
import CourseList from './pages/CourseList';
import CourseDetail from './pages/CourseDetail';
import Profile from './pages/Profile';
import { speak, startListening, stopListening } from './utils/voiceUtils';
import axios from 'axios';

axios.defaults.baseURL = 'http://localhost:5000/api';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));
  const [currentPage, setCurrentPage] = useState('login');
  const [listening, setListening] = useState(false);

  useEffect(() => {
    if (!listening) {
      startListening(handleVoiceCommand);
      setListening(true);
    }
    return () => stopListening();
  }, [listening]);

  const handleVoiceCommand = (command) => {
    speak(`You said: ${command}`); // Feedback
    if (command.includes('help')) {
      speak('Available commands: login, register, home, courses, profile, logout, repeat, volume up, volume down.');
    } else if (command.includes('login')) {
      setCurrentPage('login');
      speak('Navigating to login.');
    } else if (command.includes('register')) {
      setCurrentPage('register');
      speak('Navigating to register.');
    } else if (command.includes('home')) {
      setCurrentPage('home');
      speak('Navigating to home.');
    } else if (command.includes('courses')) {
      setCurrentPage('courses');
      speak('Navigating to courses.');
    } else if (command.includes('profile')) {
      setCurrentPage('profile');
      speak('Navigating to profile.');
    } else if (command.includes('logout')) {
      localStorage.removeItem('token');
      setIsLoggedIn(false);
      setCurrentPage('login');
      speak('Logged out.');
    } else if (command.includes('repeat')) {
      // Logic to repeat last spoken text (implement as needed)
      speak('Repeating last message.');
    } else if (command.includes('volume up')) {
      // TTS volume not directly controllable, but simulate
      speak('Volume increased.');
    } else if (command.includes('volume down')) {
      speak('Volume decreased.');
    } else {
      // Pass command to current page's handler
      window.dispatchEvent(new CustomEvent('voiceCommand', { detail: command }));
    }
  };

  const renderPage = () => {
    if (!isLoggedIn) {
      if (currentPage === 'register') return <Register setIsLoggedIn={setIsLoggedIn} />;
      return <Login setIsLoggedIn={setIsLoggedIn} />;
    }
    switch (currentPage) {
      case 'home': return <Home />;
      case 'courses': return <CourseList />;
      case 'profile': return <Profile />;
      default: return <Home />;
    }
  };

  return (
    <Router>
      <div role="main" aria-label="Accessible Learning Platform">
        <h1 hidden>Accessible Learning Platform for the Blind</h1>
        {renderPage()}
      </div>
      <Route path="/" element={<Home />} />
      <Route path="/courses" element={<CourseList />} />
      <Route path="/course/:id" element={<CourseDetail />} />
      <Route path="/profile" element={<Profile />} />
    </Router>
  );
};

export default App;
