import React, { useEffect } from 'react';
import { speak } from '../utils/voiceUtils';

const Home = () => {
  useEffect(() => {
    speak('Welcome home. Say courses to browse learning materials, or profile for settings.');
  }, []);

  return <div aria-live="polite">Home Page</div>;
};

export default Home
