import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { speak } from '../utils/voiceUtils';

const Register = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [step, setStep] = useState(0);

  useEffect(() => {
    speak('Welcome to register. Say your username.');
    const handleCommand = (e) => {
      const command = e.detail;
      if (step === 0) {
        setUsername(command);
        setStep(1);
        speak(`Username set to ${command}. Now say your password.`);
      } else if (step === 1) {
        setPassword(command);
        setStep(2);
        speak(`Password set. Say register to submit.`);
      } else if (step === 2 && command.includes('register')) {
        handleRegister();
      }
    };
    window.addEventListener('voiceCommand', handleCommand);
    return () => window.removeEventListener('voiceCommand', handleCommand);
  }, [step]);

  const handleRegister = async () => {
    try {
      await axios.post('/auth/register', { username, password });
      speak('Registration successful. Now log in.');
      // Redirect to login via voice or auto
    } catch (err) {
      speak('Registration failed. Try again.');
      setStep(0);
    }
  };

  return <div aria-live="polite">Register Page (Voice Controlled)</div>;
};

export default Register;
